		<script>
			document.addEventListener("DOMContentLoaded", () => {
				const counters = document.querySelectorAll(".counter");
				counters.forEach((counter) => {
					const updateCount = () => {
						const target = +counter.getAttribute("data-target");
						const count =
							parseFloat(
								counter.innerText.replace(/[^0-9.]/g, "")
							) || 0;
						const increment = target / 200;

						if (count < target) {
							counter.innerText = (count + increment).toFixed(1);
							setTimeout(updateCount, 10);
						} else {
							counter.innerText =
								target % 1 === 0 ? target : target.toFixed(1);
							if (counter.getAttribute("data-target") === "130") {
								counter.innerText = `$${counter.innerText}B`;
							} else if (counter.getAttribute("data-target") === "350") {
								counter.innerText = `${counter.innerText}K`;
							} else if (counter.getAttribute("data-target") === "24") {
								counter.innerText = `${counter.innerText}/7`;
							} else if (counter.getAttribute("data-target") === "98.9") {
								counter.innerText = `${counter.innerText}%`;
							} else {
								counter.innerText = `${counter.innerText}`;
							}
						}
					};
					updateCount();
				});
			});
		</script>

